mapper.select:
SELECT member_Id, name, password, address, phone,receive_Email
FROM member_tb
WHERE member_id = #{memberId}

SELECT member_Id, name, password, address, phone,receive_Email
FROM member_tb
WHERE phone = #{phone}

SELECT member_Id, name, password, address, phone,receive_Email
FROM member_tb
ORDER BY member_name = #{name}

SELECT member_Id, name, password, address, phone, receive_Email
FROM member_tb
WHERE member_id = #{memberId}

mapper.update:
	UPDATE member_tb
	SET member_id=#{memberId} ,member_address=#{address}, member_phone=#{phone},receive_Email=#{receiveEmail}
	WHERE member_id= #{memberId}
