mapper.select:
SELECT id, name, password, address, phoneNumber,receiveEmail
FROM member_tb
WHERE member_id = #{id}

SELECT id, name, password, address, phoneNumber,receiveEmail
FROM member_tb
WHERE member_phoneNumber = #{phoneNumber}

SELECT id, name, password, address, phoneNumber, receiveEmail
FROM member_tb
WHERE member_id = #{id}

mapper.update:
	UPDATE member_tb
	SET member_id=#{id} ,member_address=#{address}, member_phoneNumber=#{phoneNumber},receiveEmail=#{receiveEmail}
	WHERE member_id= #{id}
